import React from 'react'
import {sendmessage_AC, type_message_info__AC} from "../../data/DIalogsReduser";
import Dialogs from "./Dialogs";



const DialogsContainer = (props) => {
    let state = props.store.getState().MessagesData
    let OnSendMessage = () => {
        props.store.dispatch(sendmessage_AC())

    }

    let OnMessageChange = (MessageText) => {
        props.store.dispatch(type_message_info__AC(MessageText))
    }

    return (

<Dialogs OnSendMessage={OnSendMessage}
         OnMessageChange={OnMessageChange}
         MessagesData={state}/>
    )
}

export default DialogsContainer;
