import React from 'react'
import d from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {Field, reduxForm} from "redux-form";
import {required,MaxLengthCreator} from "../utils/validators"
import {TextArea} from "../assetss/common/ValidatorsComponents/ElementsValidators"


const  maxValue300 = MaxLengthCreator(300)

const DialogsForm = (props) =>{

    {props.AllDialogs ? props.AllDialogs : "now is empty"}

    let dialogsElement = props.MessagesData.users.map(d => <DialogItem key={d.id} name={d.name} id={d.id} avatars={props.MessagesData.avatars}/>);
    let messagesElements = props.MessagesData.messages.map(m => < Message key={m.id} message={m.message} id={m.id}/>);

    return(

        <form onSubmit={props.handleSubmit}>
        <div className={d.dialogs}>
            <div className={d.dialogsItems}>
                {dialogsElement}
            </div>
            <div className={d.messages}>

                {messagesElements}

                <div>
                    <div>
                      <Field  name={"MessageTextArea"}
                              component={TextArea}
                              placeholder={"Enter your message here"}
                              validate={[required,maxValue300]}/>
                    </div>
                    <div>
                        <button className={d.button}> send message </button>
                    </div>
                </div>
            </div>

        </div>
        </form>
    )
}

const ReduxDialogsForm = reduxForm({form: 'dialogs'})(DialogsForm)


const Dialogs = (props) => {
    let SendMessage = (data)=>{
        props.sendmessage(data.MessageTextArea)
    }

    return (<div>
            <h1 className={d.header}>My messages</h1>
       <ReduxDialogsForm onSubmit={SendMessage} {...props} />
        </div>
    )
}
export default Dialogs;
