import React from 'react'
import d from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {sendmessage_AC, type_message_info__AC} from "../../data/datas";

let SendMessage = () => {
    props.dispatch(sendmessage_AC ())

}


let OnPostChange= () =>{
    let text = NewMessage.current.value
    props.dispatch(type_message_info__AC(text))
}


const Dialogs = (props) => {
    let dialogsElement = props.dialogs.map(d => <DialogItem
        name={d.name}
        id={d.id}
        avatars={props.avatars}/>);
    let messagesElements = props.messages.map(m => < Message
        message={m.message}
        id={m.id}
        dispatch={props.dispatch}
        NewMessageText={props.NewMessageText}/>);
    return (
        <div className={d.dialogs}>
            <div className={d.dialogsItems}>
                {dialogsElement}
            </div>
            <div className={d.messages}>

                {messagesElements}
            </div>
        </div>
    )
}
export default Dialogs;
