const SET_AUTH_USER = 'SET_AUTH_USER'

let DefaultState = {
    userId:null,
    email:null,
    login:null,
    IsLogined:false
}

const DialogsReducer = (state = DefaultState, action) => {
    switch (action.type) {

      case SET_AUTH_USER : {
          return {
                ...state,
                ...{userId,email,login},
              IsLogined:true
                }

        }
        default :
            return state

    }
}

export const SetAuthUser_AC = (userId,email,login) => {
    return {type: SET_AUTH_USER,}
}
export default DialogsReducer
