import React from "react"
import {Input,CreateField,TextArea} from "../../assetss/common/ValidatorsComponents/ElementsValidators";
import pc from './UserInfo.module.css'
import {MaxLengthCreator} from "../../utils/validators";

const ContactsEditorForm = (props) => {


    const maxLength50 = MaxLengthCreator(50)
    const maxLength30 = MaxLengthCreator(30)

    return <div>
        <form onSubmit={props.handleSubmit}>
            <button onClick={props.ContactsEditingOFF} className={pc.editButton}>
                save changes
            </button>
            <div>
                My name is :
                {CreateField("Enter your full name","fullName",[],Input)}
            </div>

            <div>
                are u looking for a job? :
                {CreateField("","lookingForAJob",[],Input,{type : "checkbox"})}
            </div>
            <div>
                type some description about your looking:
                {CreateField("Enter description","lookingForAJobDescription",[],Input)}
            </div>
            <div>
                about me:
                {CreateField("aboutMe","type something abut you",[],TextArea)}
            </div>


            {props.error
                ?
                <div className={pc.FormError}>
                    {props.error}
                </div>
                : null}

        </form>
    </div>
}

export default ContactsEditorForm
