import React, {useState, useEffect} from "react"
import CreateNewToDoList from "./CreateNewToDoList/CreateNewToDoList";
import TDLS from "./ToDoLists.module.css"
import ToDoListMenuIcon from "./ListMenu";


const ToDoLists = (props) => {
    let [EditTitleMode, SetEditTitleMode] = useState(false)
    let[CurrentTitle,SetCurrentTitle] = useState(props.ToDoLists.title)

const EditTitleModeON = ()=>{
    SetEditTitleMode(true)
}
    const EditTitleModeOFF = ()=>{
        SetEditTitleMode(false)
    }
    useEffect(() => {
            SetStatus(props.status)
        }, [props.status]
    )
    return <div>
        <CreateNewToDoList AddNewToDoListsThunk={props.AddNewToDoListsThunk}/>
        {props.ToDoLists &&
        props.ToDoLists.map(key => {
            return <div className={TDLS.ToDoListsPosition} key={key.id}>

                <div className={TDLS.ToDolistsWrapper}>

                    <ToDoListMenuIcon ListID={key.id}
                                      Del={props.DeleteToDoListThunk}
                    />

                    <div className={TDLS.ToDoListTitle}>
                        {key.title}
                    </div>
                </div>
            </div>
        })
        }
    </div>
}

export default ToDoLists
