    return <div>

        <form>
            <div>
                <Field name={'login'} placeholder={"Enter the login"} component={"input"}/>

            </div>

            <div>
                <Field name={'password'} placeholder={"Enter the password"} component={"input"}/>

            </div>

            <div>
                <Field name={'rememberMe'} component={"input"} type={"checkbox"}/> remember me
            </div>

            <div>
                <button> Enter </button>
            </div>
        </form>
    </div>
}
const ReduxLoginForm = reduxForm({form : 'login'}) (LoginForm)

