import React from "react"

class ProfileStatus extends React.Component {

    StatusState = {
        StatusChanging: false

    }
StatusChangingON  (){
this.setState({
    StatusChanging: true
})
    }

    StatusChangingOFF (){
        this.setState({
            StatusChanging: false
        })
    }
    render() {

        return (
            <div>
                {!this.StatusState.StatusChanging ?
                    <div>
                         <span  onDoubleClick={ this.StatusChangingON.bind(this)() } >

                       {this.props.status}
                        </span>

                    </div>
                    :
                    <div>
                        <input value={this.props.status}/>
                    </div>
                }
            </div>
        )
    }
}

export default ProfileStatus
