import React from "react"
import {Input,CreateField,TextArea,TextAreaAboutMe} from "../../assetss/common/ValidatorsComponents/ElementsValidators";
import pc from './UserInfo.module.css'
import {MaxLengthCreator} from "../../utils/validators";
import {reduxForm} from "redux-form";

const ProfileDataEditorForm = (props) => {
    return <div>
        <form >
            <div>
                <b>My name is : </b>
                {CreateField("Enter your full name","fullName",[],Input)}
            </div>

            <div>
                <b>are u looking for a job? :</b>
                {CreateField("","lookingForAJob",[],Input,{type : "checkbox"})}
            </div>
            <div>
                <b>My professional skills:</b>
                {CreateField("Enter description","lookingForAJobDescription",[],TextAreaAboutMe)}
            </div>
            <div>
                <b> about me:</b>
                {CreateField("print something about you","aboutMe",[],TextAreaAboutMe)}
            </div>


            {props.error
                ?
                <div className={pc.FormError}>
                    {props.error}
                </div>
                : null}

        </form>
    </div>
}
const ProfileDataEditorFormRedux = reduxForm({form: 'ProfileDataEditorForm'})(ProfileDataEditorForm)
export default ProfileDataEditorFormRedux
