import React,{useState} from 'react'
import Paginator from "../assetss/common/Paginator/Paginator";
import User from "./User";
import FindUser from "./FindUser";
import FUStyles from "./FindUser.module.css";



let PurifyUsers = (props) => {
let [FoundingMode, SetFoundingMode] = useState(false)

    const FoundingModeON = () =>{
      SetFoundingMode(true)

    }
    const FoundingModeOFF = () =>{
        SetFoundingMode(false)
    }

    {props.ChangePortionSize(15)}
    return <div>

        {!FoundingMode ?
            <button onClick={FoundingModeON}> find user </button>
            :
            <>
            <FindUser FoundingModeOFF={FoundingModeOFF}/>
                &nbsp;&nbsp; <button className={FUStyles.FindUserButtons}
                                     onClick={FoundingModeOFF}>
                go back </button>
            </>
        }

        { props.Users.map(us =>  <User
            user={us}
            key={us.id}
            InProcess={props.InProcess}
            OnUnFollow={props.OnUnFollow}
            OnFollow={props.OnFollow}
            IsLogined={props.IsLogined}
        />)
        }

       <Paginator OnChangedPage={props.OnChangedPage}
                  currentPage={props.currentPage}
                  totalUsersCount={props.totalUsersCount}
                      pageSize={props.pageSize}
                  PortionSize={props.PortionSize}
       />
    </div>
}
export default PurifyUsers
