import React from 'react';
import pc from './MyPosts.module.css'
import Post from "./Post/Post";

const MyPosts = (props) => {

    let Posts = props.posts.map(p => <Post message={p.Post} likecount={p.likesCount}/>);
    let NewPostEl = React.createRef();



    let addPost = () => {
        props.dispatch({type : 'ADD-POST'})

    }


    let OnPostChange= () =>{
        let text = NewPostEl.current.value
        props.dispatch({type : 'TYPES-POST-INFO', newtext : text})
    }

    return (

        <div className={pc.pp}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea value={props.TextNewPost} ref={NewPostEl} onChange={OnPostChange} />
                </div>
                <div>
                    <button onClick={addPost}> add post</button>
                </div>
            </div>
            <div className={pc.marginPost}>
                {Posts}
            </div>
        </div>
    );
};
export default MyPosts;
