                        <div className={ts.TaskTitle}>
                            {task.title}
                        </div>
                    </div>

                    <div className={ts.TaskHeaderWrapper}>

                        <div>
                            start date
                        </div>

                        <div className={ts.deadline}>
                            deadline
                        </div>
                    </div>

                    <div className={BodyStyles}>

                        <div >
                            icon status
                        </div>

                        <div className={ts.description}>
                            descriptionwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwwww
                        </div>

                        <div>
                            icon to trash
                        </div>
                        </div>
                    </div>
            })
        }
    </div>
}

export default Tasks
