import React from 'react'
import {connect} from "react-redux";
import Users from "./Users";
import {follow_AC, setUsers_AC, unfollow_AC} from "../../data/UsersReduser";

let StateToProps = (state)=>{
    return{
        UsersData: state.UsersData
    }
}

let DispatchToProps = (dispatch)=>{
    return{onFollow: (userID)=>{

            dispatch(follow_AC(userID))
        },
        onUnFollow: (userID) =>{

            dispatch(unfollow_AC(userID))
        },
        setUsers:(Users)=>{
        dispatch(setUsers_AC(Users))
        }
    }
}

const UsersContainer = connect(StateToProps,DispatchToProps)(Users)

export default UsersContainer
