import {API} from "../API/requests";

const SET_USER_DATA = 'SET_USER_DATA'
const IsFetching_Toggle = 'IsFetching_Toggle'
let DefaultState = {
    userId:null,
    email:null,
    login:null,
    IsLogined:false,
    IsFetching:false
}

const AuthReducer = (state = DefaultState, action) => {
    switch (action.type) {

        case SET_USER_DATA : {
          return {
                ...state,
                ...action.data,
              IsLogined:true
                }

        }
        case IsFetching_Toggle:{
            return {...state, IsFetching: action.IsFetching}
        }
        default :
            return state

    }
}

export const SetAuthUser = (userId,email,login) =>
    ({type: SET_USER_DATA,data:{userId,email,login}})

export const ToggleIsFetching = (IsFetching) => {
    return {type : IsFetching_Toggle, IsFetching}
}
export  const AuthMeThunk = () =>{
    return (dispatch)=>{
        ToggleIsFetching(true)
        API.AuthMe().then(data => { //возвращает данные авторизованного юзера
          dispatch(ToggleIsFetching(false))
            if (data.resultCode === 0){
                let {id,email,login} = data.data
             SetAuthUser(id,email,login)

            }
        })
    }
}
export default AuthReducer
