import React from 'react'
import um from './Users.module.css'
import * as axios from 'axios'


const Users = (props) => {

    if(props.Users.length === 0){
axios.get("https://social-network.samuraijs.com/api/1.0/users").then(responce=>{
    props.setUsers(responce.data.items)
    }
)
    }
    return (
        props.Users.map(us => <div key={us.id} className={um.Main}>
            <div>
                <img className={um.img} src={us.photos}/>
                <div>
                    {us.followed
                        ? <button onClick={ () => {props.onUnFollow(us.id)}} className={um.button}>
                            unfollow
                        </button>
                        : <button onClick={ () => {props.onFollow(us.id)}} className={um.button}>
                            follow
                        </button>}
                </div>
            </div>
            <div className={um.Names}>
                {us.name}
                <div className={um.Posts}>
                    {us.status}
                </div>
            </div>
            <div className={um.Country}>
                {'country'}
                {','}
                <div className={um.City}>
                    {'city'}
                </div>
            </div>
        </div>)
    )
}
export default Users
