import React from 'react';
import Profile from "./Profile";
import {connect} from "react-redux";
import { withRouter} from 'react-router-dom'
import {GetProfileThunk,GetProfileStatusThunk,SetProfileStatusThunk,SetProfilePhotoThunk} from '../../data/ProfileReduser'

import {compose} from "redux";


class ProfileContainer extends React.Component {

    ProfileDataToPaint(){
        let ProfileID = this.props.match.params.userID
        if(!ProfileID){
            ProfileID = this.props.userID
            if(!ProfileID){
                this.props.history.push("/login")
            }
        }
        this.props.GetProfileThunk(ProfileID)
        this.props.GetProfileStatusThunk(ProfileID)
    }

    componentDidMount() {
        this.ProfileDataToPaint()
    }

    componentDidUpdate(prevProps, prevState, snapshot) {

        if(this.props.match.params.userID !== prevProps.match.params.userID ){
            this.ProfileDataToPaint()
        }
    }

    render() {

        return (
            <Profile  profile={this.props.profile}
                     status={this.props.status}
                     SetProfileStatus={this.props.SetProfileStatusThunk}
                      SetProfilePhoto={this.props.SetProfilePhotoThunk}
                        />
        )
    }
}

let MapStateToProps = (state) => ({
    profile: state.ProfileData.profile,
    status : state.ProfileData.status,
    userID : state.Auth.userId,
    IsMyPage : state.ProfileData.IsMyPage})



export default compose(
    connect(MapStateToProps, {GetProfileThunk,GetProfileStatusThunk,
        SetProfileStatusThunk,SetProfilePhotoThunk}),
    withRouter)(ProfileContainer)
