export const required = (value) =>{
if(value ){
    return undefined
}

else return "This field is required"
}

export const NaxLengthCreator = (maxvalue) =>{
    return(value)=>{
if(value.length > maxvalue) {
    return `Maximal length is a ${maxvalue} symbols`
}
else return undefined
    }
}
