import React from "react"
import {
    Input,
    CreateField,
    TextAreaAboutMe,
    ContactFormInput,InputName
} from "../../assetss/common/ValidatorsComponents/ElementsValidators";

import {MaxLengthCreator, required} from "../../utils/validators";


const max300=MaxLengthCreator(300)
const max150=MaxLengthCreator(150)
const max50=MaxLengthCreator(50)

const TaskEditForm = (props) => {

    return <div>

        <form onSubmit={props.handleSubmit}>
            <button >
                Save changes
            </button>
            <button  onClick={props.TaskEditModeOFF}>
                go back
            </button>


            <div >
                {Object.keys(props.CurrentTask).map(key => {
                    return <div key={key}>
                        {key} : {CreateField("key","", [max150],<input/> )}
                    </div>
                })}
            </div>

        </form>
    </div>
}

export default TaskEditForm
