import React from 'react'
import d from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {Field, reduxForm} from "redux-form";

const DialogsForm = () =>{

    let dialogsElement = props.MessagesData.users.map(d => <DialogItem
        name={d.name}
        id={d.id}
        avatars={props.MessagesData.avatars}/>);
    let messagesElements = props.MessagesData.messages.map(m => < Message
        message={m.message}
        id={m.id}/>);

    return(
        <form onSubmit={props.handleSubmit}>
        <div className={d.dialogs}>
            <div className={d.dialogsItems}>
                {dialogsElement}
            </div>
            <div className={d.messages}>

                {messagesElements}

                <div>
                    <div>
                      <Field  name={"MessageTextArea"}
                              component={"textarea"}
                              placeholder={"Enter your message here"} />
                    </div>
                    <div>
                        <button> send message </button>
                    </div>
                </div>
            </div>

        </div>
        </form>
    )
}

const ReduxDialogsForm = reduxForm({form: 'dialogs'})(DialogsForm)

const Dialogs = (props) => {

const onSubmit = ()=>{

}

    return (

       <ReduxDialogsForm onSubmit={onSubmit} />
    )
}
export default Dialogs;
