import React, {useEffect,useState} from "react"
import Preloader from "../../assetss/common/Loader/Loader";
import {NavLink} from "react-router-dom";
import {MaxLengthCreator, required} from "../../utils/validators";
import d from "../Dialogs.module.css";
import {Field, reduxForm} from "redux-form";
import {FindUserFormInput} from "../assetss/common/ValidatorsComponents/ElementsValidators";


const maxValue50 = MaxLengthCreator(50)

const FindUserFom = (props) => {

    let [FoundedUser,SetFoundedUser] = useState(props.FoundedUser)

    useEffect(() => {
            SetFoundedUser(props.FoundedUser)
        }, [props.FoundedUser]
    )

    return (
        <form onSubmit={props.handleSubmit}>
            <div >

                <div className={d.messages}>
                    <div>
                        <div>
                            <Field name={"MessageTextArea"}
                                   component={FindUserFormInput}
                                   placeholder={"Enter your message here"}
                                   validate={[required, maxValue50]}/>
                        </div>
                        <div>
                            <button className={d.button}> send message</button>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    )
}

const ReduxMessageWithUserForm = reduxForm({form: 'FindUser'})(FindUserFom)


const FindUser = (props) => {



    return <div>
        <ReduxMessageWithUserForm />
    </div>

}
export default FindUser
