import React from 'react'
import d from './Dialogs.module.css';
import {Field, reduxForm} from "redux-form";
import {required, MaxLengthCreator} from "../utils/validators"
import {TextArea} from "../assetss/common/ValidatorsComponents/ElementsValidators"
import DialogInfo from "./DialogInfo";

const maxValue300 = MaxLengthCreator(300)

const DialogsForm = (props) => {


    return (
        <form onSubmit={props.handleSubmit}>
            <div className={d.dialogs}>
                
                <div className={d.messages}>
                    <div>
                        <div>
                            <Field name={"MessageTextArea"}
                                   component={TextArea}
                                   placeholder={"Enter your message here"}
                                   validate={[required, maxValue300]}/>
                        </div>
                        <div>
                            <button className={d.button}> send message</button>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    )
}

const ReduxDialogsForm = reduxForm({form: 'dialogs'})(DialogsForm)


const Dialogs = (props) => {

    const SendNewMessage = (Formdata) => {
        props.SendNewMessageThunk(1671, Formdata.MessageTextArea)
    }
    return (<div >

            {props.MessagesData.AllDialogs.map(el => {
                return <DialogInfo key={el.id} DialogUserName={el.userName}
                               DialogUserAvatar={el.photos.small ?
                                   el.photos.small
                                   : el.photos.large}
                               NewMessagesCount={el.newMessagesCount}
                               MessageSendTime={el.lastDialogActivityDate}
                               UserID={el.id}
                />
            })}

           {/* <ReduxDialogsForm onSubmit={SendNewMessage} {...props} />*/}
        </div>
    )
}
export default Dialogs;
