import React from 'react'
import um from './Users.module.css'
import * as axios from 'axios'
import UserPhoto from '../assetss/images/userPhoto.jpg'


let Users = (props) => {

    let getUsers = () => {

    if (props.Users.length === 0) {
        axios.get("https://social-network.samuraijs.com/api/1.0/users")
            .then(responce => {
                props.setUsers(responce.data.items)
            })
    }
}
    return (<div>
            <button onClick={getUsers}>get users</button>

            props.Users.map(us => <div key={us.id} className={um.Main}>
                <div>
                    <img className={um.img} src={us.photos.small != null ? us.photos.small : UserPhoto}/>
                    <div>
                        {us.followed
                            ? <button onClick={() => {
                                props.onUnFollow(us.id)
                            }} className={um.button}>
                                unfollow
                            </button>
                            : <button onClick={() => {
                                props.onFollow(us.id)
                            }} className={um.button}>
                                follow
                            </button>}
                    </div>
                </div>
                <div className={um.Names}>
                    {us.name}
                    <div className={um.Posts}>
                        {us.status}
                    </div>
                </div>
                <div className={um.Country}>
                    {'country'}
                    {','}
                    <div className={um.City}>
                        {'city'}
                    </div>
                </div>
            </div>)
            < /div>

    )


}
export default Users
