import React from 'react';
import {addpost_AC, type_post_info__AC} from "../../../data/ProfileReduser";
import MyPosts from "./MyPosts";
import {connect} from "react-redux";

let StateProps = (state) =>{
    return{posts:state.ProfileData.Posts,
        TextNewPost:state.ProfileData.TextNewPost
    }
}

let DispatchProps = (dispatch) =>{
       return{
       updatePostText: (text)=>{

           dispatch(type_post_info__AC(text))
       },
       addPost: ()=>{
           dispatch(addpost_AC())

       }
   }
}
const MyPostsContainer = connect(StateProps,DispatchProps)(MyPosts)

export default MyPostsContainer;
