let [MessageRestored,SetMessageRestored]=useState(false)

    return <div>
        {
            <button onClick={()=>{SetToConfirmDeleting(true)}}> delete this shit </button>
        }
        {ToConfirmDeleting &&
            <div>
                are you sure?
                <div>
                    <button onClick={() => {
                        SetMessageRestored(true)
                        SetToConfirmDeleting(false)
                    }}>
                        yes
                    </button>
                    <button onClick={()=>{
                        SetToConfirmDeleting(false)}
                    }>
                        no
                    </button>
                </div>
            </div>
        }
        {MessageRestored &&
            <button> restore this shit </button>
        }

        </div>

}

export default DeleteMessage
