let DefaultState = {
    userID:null,
    
}

const DialogsReducer = (state = DefaultState, action) => {
    switch (action.type) {

      case type_message_info : {
          return {
                ...state,
                NewMessageText:action.NewMessageText
                }

        }
        case  send_message : {
            let body = state.NewMessageText
            return{...state,
                NewMessageText: '',
                messages:[...state.messages,{id: 6, message: body}]
            }
        }
        default :
            return state

    }
}
export const type_message_info__AC = (text) => {
    return {type: type_message_info, NewMessageText: text}
}
export const sendmessage_AC = () => {
    return {type: send_message}
}
export default DialogsReducer
