const follow = 'FOLLOW'
const unfollow = 'UNFOLLOW'
const setusers = 'SET_USERS'
const Set_Current_Page= 'SET_CURRENT_PAGE'
const Set_Total_Count_Of_Users = 'Set_Total_Count_Of_Users'
const IsFetching_Toggle = 'Toggle_is_fetching'

let DefaultState = {
        Users: [],
        pageSize : 5,
        totalUsersCount:0,
        currentPage : 1,
         IsFetching : false
}

const UsersReducer =  (state = DefaultState,action) => {
    switch (action.type) {
        case follow: {
        return {
          ...state,
          Users:state.Users.map(us => {
              if (us.id === action.userID) {
                  return {
                      ...us,
                      followed:true
                  }
              }
              return us
          })
        }
    }
        case unfollow: {
            return {
                ...state,
                Users: state.Users.map(us =>{
                    if(us.id === action.userID){
                        return {
                            ...us,
                            followed:false
                        }
                    }
                    return us
                })
            }
        }
        case setusers:{
            return {...state, Users: action.users}
        }
        case Set_Current_Page:{
            return {...state, currentPage: action.currentPage}
        }
        case Set_Total_Count_Of_Users:{
            return {...state, totalUsersCount: action.totalUsersCount}
        }
        case IsFetching_Toggle:{
            return {...state, IsFetching: action.IsFetching}
        }
        default :
            return state
    }
}
export const OnFollow = (userID) => {
    return {type : follow,userID:userID }
}
export const onUnFollow = (userID) => {
    return {type : unfollow, userID:userID }
}
export const setUsers = (users) => {
    return {type : setusers, users:users }
}
export const SetCurrentPage = (currentPage) => {
    return {type : Set_Current_Page, currentPage: currentPage}
}
export const SetTotalCountOfUsers = (totalCount) => {
    return {type : Set_Total_Count_Of_Users, totalUsersCount: totalCount}
}
export const ToggleIsFetching = (IsFetching) => {
    return {type : IsFetching_Toggle, IsFetching}
}
export default UsersReducer
