import React from 'react'
import {sendmessage_AC, type_message_info__AC} from "../../data/DIalogsReduser";
import Dialogs from "./Dialogs";
import {connect} from "react-redux";

let StateToProps = (state)=>{
    return{
        MessagesData: state.MessagesData,
        IsAuth : state.Auth.IsAuth
    }
}

let DispatchToProps = (dispatch)=>{
    return{OnSendMessage: ()=>{
        dispatch(sendmessage_AC())
        },
        OnMessageChange: (MessageText) =>{
        dispatch(type_message_info__AC(MessageText))
        }
    }
}
 const DialogsContainer = connect(StateToProps,DispatchToProps)(Dialogs)

export default DialogsContainer
