import React from 'react';
import Profile from "./Profile";
import {connect} from "react-redux";
import { withRouter} from 'react-router-dom'
import {GetProfileThunk,GetProfileStatusThunk,SetProfileStatusThunk} from '../../data/ProfileReduser'
import {AuthRedirect} from "../hocs/AuthRedirect";
import {compose} from "redux";


class ProfileContainer extends React.Component {
    componentDidMount() {

        let ProfileID = this.props.match.params.userID
        if(!ProfileID){
            ProfileID = 2
        }
      this.props.GetProfileThunk(ProfileID)
      this.props.GetProfileStatusThunk(ProfileID)



    }

    render() {

        return (
            <Profile {...this.props} profile={this.props.profile}
                     status={this.props.status}
                     SetProfileStatus={this.props.SetProfileStatusThunk}/>
        )
    }
}

let MapStateToProps = (state) => ({profile: state.ProfileData.profile,
    status : state.ProfileData.status})

// тут объект обернут скобками т.к мы хотим что бы функция вернула объект со свойством
/*тут короче понадобилось достучаться до линка и взять от туда айди юзера
* что бы это сделать можно конечно пойти и взять всю строку урла, разбить её на части и тд. но есть
* свпособ проще, так же как и коннект существует метод WithRouter, он прокинет в пропсах нужную нам инфу*/

export default compose(
    connect(MapStateToProps, {GetProfileThunk,GetProfileStatusThunk,SetProfileStatusThunk}),
    withRouter,
    AuthRedirect)(ProfileContainer)
