import React from 'react';
import * as axios from "axios";
import Header from "./Header";
import Preloader from "../assetss/common/Loader/Loader";
import {connect} from "react-redux";
import {SetAuthUser,ToggleIsFetching} from "../../data/AuthReducer";
import {AuthMe} from "../../API/requests"


class HeaderContainer extends React.Component {
    componentDidMount() {
        // это что-то типо конструктора, принято все вещи, которые нарушают идею чистых функцию писать тут
        this.props.ToggleIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`,{
            /*короче, этот параметр мы передаем так как кука не
           цнпляется если мы авторизованы на одном домене, к примеру сервере,
            и шлём запрос с другого домена, ну к примеру с локалхоста , который
            дамет нодеДжс и вот что бы эта кука цапеилась указываем withCredentials : true
            */
            withCredentials : true
        }).then(response => { //возвращает данные авторизованного юзера
                this.props.ToggleIsFetching(false)
                if (response.data.resultCode === 0){
                    let {id,email,login} = response.data.data
                    this.props.SetAuthUser(id,email,login)
                }
            })
    }

    render() {
        return <>
        {this.props.IsFetching ? <Preloader/> :null}
          <Header {...this.props} />
          </>
    }

}
let MapStateToProps = (state) =>
    ({IsFetching : state.Auth.IsFetching,
    IsLogined:state.Auth.IsLogined,
    login:state.Auth.login})

export default HeaderContainer = connect(MapStateToProps, {SetAuthUser,ToggleIsFetching})(HeaderContainer);
