import React from 'react'
import {connect} from "react-redux";
import {
    OnFollow,
    setUsers,
    onUnFollow,
    SetCurrentPage,
    SetTotalCountOfUsers,
    ToggleIsFetching
} from "../../data/UsersReduser";
import * as axios from "axios";
import PurifyUsers from "./Users";
import Preloader from "../assetss/common/Loader/Loader";

class UsersAPItoServer extends React.Component {
    /* this component using for make requests on server for a new users and drawing
Ui component (Users)*/

    componentDidMount() {
        // это что-то типо конструктора, принято все вещи, которые нарушают идею чистых функцию писать тут
        this.props.ToggleIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${this.props.currentPage}
    &count=${this.props.pageSize}`)
            .then(responce => {
                this.props.ToggleIsFetching(false)
                this.props.setUsers(responce.data.items)
                this.props.SetTotalCountOfUsers(responce.data.totalCount)
            })
    }

    OnChangedPage = (pageNumber) => {
        this.props.SetCurrentPage(pageNumber)
        this.props.ToggleIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${pageNumber}
    &count=${this.props.pageSize}`)
            .then(responce => {
                this.props.ToggleIsFetching(false)
                this.props.setUsers(responce.data.items)
            })
    }

    render() {

        return <>
            {this.props.IsFetching ? <Preloader/> :null}
        <PurifyUsers Users={this.props.Users}
                            totalUsersCount={this.props.totalUsersCount}
                            pageSize={this.props.pageSize}
                            OnChangedPage={this.OnChangedPage}
                            onFollow={this.props.OnFollow}
                            onUnFollow={this.props.onUnFollow}
                            currentPage={this.props.currentPage}/>
                            </>
    }

}

let StateToProps = (state) => {
    return {
        Users: state.UsersData.Users,
        pageSize: state.UsersData.pageSize,
        totalUsersCount: state.UsersData.totalUsersCount,
        currentPage: state.UsersData.currentPage,
        IsFetching : state.UsersData.IsFetching
    }
}

/*let DispatchToProps = (dispatch) => {
    return {
        onFollow: (userID) => {

            dispatch(follow_AC(userID))
        },
        onUnFollow: (userID) => {

            dispatch(unfollow_AC(userID))
        },
        setUsers: (Users) => {
            dispatch(setUsers_AC(Users))
        },
        SetCurrentPage: (NumberOfPage) => {
            dispatch(setCurrentPage_AC(NumberOfPage))
        },
        SetTotalCountOfUsers: (totalCount) => {
            dispatch(SetTotalCountOfUsers_AC(totalCount))
        },
        ToggleIsFetching: (IsFetching) => {
            dispatch(IsFetchingToggle_AC(IsFetching))
        }
    }
}*/

const UsersContainer = connect(StateToProps,
    {OnFollow,onUnFollow,setUsers,SetCurrentPage,SetTotalCountOfUsers,ToggleIsFetching})
(UsersAPItoServer)

export default UsersContainer
