import React from 'react';
import {Field, reduxForm} from "redux-form";
import {compose} from "redux";
import {connect} from "react-redux";
import {SubmitUserDataThunk} from "../../data/LoginReduser"

const MapStateToProps =(state)=>{

}

const LoginForm = (props) => {


    return <div>

        <form onSubmit={props.handleSubmit} >
            <div>
                <Field name={'login'} placeholder={"Enter the email"} component={"input"}/>

            </div>

            <div>
                <Field name={'password'} placeholder={"Enter the password"} component={"input"}/>

            </div>

            <div>
                <Field name={'rememberMe'} component={"input"} type={"checkbox"}/> remember me
            </div>

            <div>
                <button> Enter </button>
            </div>
        </form>
    </div>
}
const ReduxLoginForm = reduxForm({form : 'login'}) (LoginForm)

const Login = (props) => {
   const  onSubmit = (formDatas) =>{
       console.log(formDatas)
props.SubmitUserDataThunk(formDatas.email,formDatas.password,formDatas.rememberMe = false)
       //here is all datas of forms, we can do here use some thunk for a make request on server
   }

    return( <div>
        <h1> Login </h1>
        <ReduxLoginForm onSubmit={onSubmit} />
    </div>)
}

export default compose(
    connect(MapStateToProps, {SubmitUserDataThunk}),
    )(Login)
