import * as axios from "axios";

export const getUsers = (currentPage,pageSize) =>{
   return  axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${currentPage}
    &count=${pageSize}`,{
    withCredentials : true
}).then(responcse =>{
    return responcse.data
   })
}

export const AuthMe =() =>{
    return axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`,{
    /*короче, этот параметр мы передаем так как кука не
   цнпляется если мы авторизованы на одном домене, к примеру сервере,
    и шлём запрос с другого домена, ну к примеру с локалхоста , который
    дамет нодеДжс и вот что бы эта кука цапеилась указываем withCredentials : true
    */
    withCredentials : true
}).then(responcse =>{
    return responcse.data
})
}

export const getProfile = (ProfileID) =>{
    return  axios.get(`https://social-network.samuraijs.com/api/1.0/profile/`+ProfileID)
.then(responcse =>{
        return responcse.data
    })
}
