import React from 'react';
import Header from "./Header";
import Preloader from "../assetss/common/Loader/Loader";
import {connect} from "react-redux";
import {SetAuthUser,ToggleIsFetching} from "../../data/AuthReducer";
import {AuthMe} from "../../API/requests"


class HeaderContainer extends React.Component {
    componentDidMount() {
        // это что-то типо конструктора, принято все вещи, которые нарушают идею чистых функцию писать тут
        this.props.ToggleIsFetching(true)
        AuthMe().then(data => { //возвращает данные авторизованного юзера
                this.props.ToggleIsFetching(false)
                if (data.resultCode === 0){
                    let {id,email,login} = data.data
                    this.props.SetAuthUser(id,email,login)
                }
            })
    }

    render() {
        return <>
        {this.props.IsFetching ? <Preloader/> :null}
          <Header {...this.props} />
          </>
    }

}
let MapStateToProps = (state) =>
    ({IsFetching : state.Auth.IsFetching,
    IsLogined:state.Auth.IsLogined,
    login:state.Auth.login})

export default HeaderContainer = connect(MapStateToProps, {SetAuthUser,ToggleIsFetching})(HeaderContainer);
