import React from 'react';
import {addpost_AC, type_post_info__AC} from "../../../data/ProfileReduser";
import MyPosts from "./MyPosts";


const MyPostsContainer = (props) => {

let state = props.store.getState()
    let addPost = () => {
        props.store.dispatch(addpost_AC())

    }

    let updatePostText= (text) =>{

        props.store.dispatch(type_post_info__AC(text))
    }

    return (<MyPosts updatePostText={updatePostText}
                     addPost={addPost}
                     posts={state.ProfileData.Posts}
                     TextNewPost={state.ProfileData.TextNewPost}/>);
};
export default MyPostsContainer;
