import React from 'react'
import d from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {sendmessage_AC, type_message_info__AC} from "../../data/DIalogsReduser";



const Dialogs = (props) => {
    let state = props.store.getState()
    let dialogsElement = state.MessagesData.users.map(d => <DialogItem
        name={d.name}
        id={d.id}
        avatars={state.MessagesData.avatars}/>);
    let messagesElements = state.MessagesData.messages.map(m => < Message
        message={m.message}
        id={m.id}/>);


    let NewMessage = React.createRef();

    let SendMessage = () => {
        props.store.dispatch(sendmessage_AC())

    }


    let OnMessageChange = (e) => {
        let MessageText = e.target.value
        props.store.dispatch(type_message_info__AC(MessageText))
    }

    return (
        <div className={d.dialogs}>
            <div className={d.dialogsItems}>
                {dialogsElement}
            </div>
            <div className={d.messages}>

                {messagesElements}

                <div>
                    <div>
                  <textarea
                      placeholder='Enter Your Message'
                      ref={NewMessage}
                      value={state.MessagesData.NewMessageText}
                      onChange={OnMessageChange}>
                        </textarea>
                    </div>
                    <div>
                        <button onClick={SendMessage}> send message</button>
                    </div>
                </div>
            </div>

        </div>
    )
}
export default Dialogs;
