const send_message = 'Dialogs/SEND-MESSAGE'

let DefaultState = {
    users: [
        {id: 1, name: 'test1'},

    ],
    messages: [
        {id: 1, message: 'hi'},

    ],
    avatars: [
        {
            id: 1,
            avalink: 'https://avatars.mds.yandex.net/get-pdb/988157/7bf97b91-4108-443f-b03a-9c0881ac5cb6/s1200?webp=false'
        }
    ],
    NewMessageText: ''
}

const DialogsReducer = (state = DefaultState, action) => {
    switch (action.type) {


        case  send_message : {
            return{...state,
                NewMessageText: action.NewMessageText,
                messages:[...state.messages,{id: 6, message: action.NewMessageText}]
            }
        }
        default :
            return state

    }
}

export const sendmessage = (text) => {
    return {type: send_message, NewMessageText:text}
}
export default DialogsReducer
