import React from 'react'
import {connect} from "react-redux";
import {compose} from "redux";
import { withRouter} from 'react-router-dom'
import {GetDialogsMessagesSelector, GetinterlocutorAvatarSelector, GetMessagesDataSelector} from "../../../data/DialogsSelectors";
import MessagesWithUser from "./MessagesWithUser";
import {AuthRedirect} from "../../hocs/AuthRedirect";
import {SendNewMessageThunk,GetNewMessagesThunk,GetInterlocutorAvatarThunk} from "../../../data/DIalogsReduser"


class MessagesContainer extends React.Component {

    componentDidMount() {
        const userID = this.props.match.params.userID
        this.props.GetNewMessagesThunk(userID)
        this.props.GetInterlocutorAvatarThunk(userID)
    }


    render() {
        return (
            <MessagesWithUser {...this.props} />
        )
    }
}

let StateToProps = (state) => ({
    MessagesData: GetMessagesDataSelector(state),
    DialogsMessages : GetDialogsMessagesSelector(state),
    InterlocutorAvatar : GetinterlocutorAvatarSelector(state)
})

export default compose(connect(StateToProps,
    {GetNewMessagesThunk,SendNewMessageThunk,GetInterlocutorAvatarThunk}),
    AuthRedirect,
    withRouter)(MessagesContainer)
