import {API} from "../API/requests";
import {stopSubmit} from "redux-form";


const SET_USER_DATA = 'Auth/SET_USER_DATA'
const IsFetching_Toggle = 'Auth/IsFetching_Toggle'

let DefaultState = {
    userId:null,
    email:null,
    login:null,
    IsLogined:false,
    IsFetching:false
}

const AuthReducer = (state = DefaultState, action) => {
    switch (action.type) {

        case SET_USER_DATA : {
          return {
                ...state,
                ...action.data
                }

        }
        case IsFetching_Toggle:{
            return {...state, IsFetching: action.IsFetching}
        }
        default :
            return state

    }
}

export const SetAuthUser = (userId,email,login,IsLogined) =>
    ({type: SET_USER_DATA,data:{userId,email,login,IsLogined}})

export const ToggleIsFetching = (IsFetching) => {
    return {type : IsFetching_Toggle, IsFetching}
}
export  const AuthMeThunk = () =>{
    return (dispatch)=>{
        ToggleIsFetching(true)
      return   API.AuthMe().then(data => { //возвращает данные авторизованного юзера
          dispatch(ToggleIsFetching(false))
            if (data.resultCode === 0){
                let {id,email,login} = data.data
            dispatch(SetAuthUser(id,email,login,true))

            }
        })
    }
}

export  const LoginThunk = (email,password,rememberMe=false) =>{
    return async (dispatch)=>{
       const data = await API.Login(email,password,rememberMe)
            if (data.resultCode === 0){
                dispatch(AuthMeThunk())
            }
            else{
                dispatch(stopSubmit("login",{_error : data.messages[0]}))
            }
    }
}

export  const LogOutThunk = () =>{
    return async (dispatch)=>{
       const data = await API.LogOut()
            if (data.resultCode === 0){
                dispatch(SetAuthUser(null,null,null,false))

            }
    }
}

export default AuthReducer
