import React from 'react'
import um from './Users.module.css'
import * as axios from 'axios'
import UserPhoto from '../assetss/images/userPhoto.jpg'
import {NavLink} from "react-router-dom";
import navc from "../NavBar/Navbar.module.css";

class Users extends React.Component {

componentDidMount() {
    axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${this.props.currentPage}
    &count=${this.props.pageSize}`)
        .then(responce => {
            this.props.setUsers(responce.data.items)
        })
}
OnChangedPage = (pageNumber)=>{
    this.props.SetCurrentPage(pageNumber)
    axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${pageNumber}
    &count=${this.props.pageSize}`)
        .then(responce => {
            this.props.setUsers(responce.data.items)
        })
}
    render() {
      let pagesCount = Math.ceil( this.props.totalUsersCount / this.props.pageSize)
        let pages = []
        for (let i= 1; i<= pagesCount; i++){
            pages.push(i)
        }
        return <div>
            <div>
                {pages.map(p => {
               return (
                   <span className={`${navc.item} ${navc.active}`}

                                onClick={(ev)=>{this.OnChangedPage(p)} }>
                   {p}
               </span >
               )})}
            </div>
            {
                this.props.Users.map(us => <div key={us.id} className={um.Main}>
                    <div>
                        <img className={um.img} src={us.photos.small != null ? us.photos.small : UserPhoto}/>
                        <div>
                            {us.followed
                                ? <button onClick={() => {
                                    this.props.onUnFollow(us.id)
                                }} className={um.button}>
                                    unfollow
                                </button>
                                : <button onClick={() => {
                                    this.props.onFollow(us.id)
                                }} className={um.button}>
                                    follow
                                </button>}
                        </div>
                    </div>
                    <div className={um.Names}>
                        {us.name}
                        <div className={um.Posts}>
                            {us.status}
                        </div>
                    </div>
                    <div className={um.Country}>
                        {'country'}
                        {','}
                        <div className={um.City}>
                            {'city'}
                        </div>
                    </div>
                </div>)
            }
        </div>
    }

}


export default Users
