import React from 'react';
import Profile from "./Profile";
import * as axios from "axios";


class ProfileContainer extends React.Component {
    componentDidMount() {
        // это что-то типо конструктора, принято все вещи, которые нарушают идею чистых функцию писать тут
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/page`)
            .then(responce => {
                this.props.ToggleIsFetching(false)
                this.props.setUsers(responce.data.items)
                this.props.SetTotalCountOfUsers(responce.data.totalCount)
            })
    }
render() {
    return <Profile {...this.props} />
}

}
export default ProfileContainer;
