import React from 'react'
import {connect} from "react-redux";
import {compose} from "redux";
import { withRouter} from 'react-router-dom'
import {
    GetDialogsMessagesSelector,
    GetinterlocutorAvatarSelector,
    GetMessagesDataSelector,
    GetUserAvatarSelector
} from "../../../data/DialogsSelectors";
import MessagesWithUser from "./MessagesWithUser";
import {AuthRedirect} from "../../hocs/AuthRedirect";
import {SendNewMessageThunk,GetNewMessagesThunk,GetInterlocutorAvatarThunk,setUserAvatarForDialogs} from "../../../data/DIalogsReduser"
import {GetProfileStatusThunk} from "../../../data/ProfileReduser";
import {GetUserIDSelector} from "../../../data/AuthSelectors";

class MessagesContainer extends React.Component {

    componentDidMount() {
        const DialoguserID = this.props.match.params.userID
        this.props.GetNewMessagesThunk(DialoguserID)
        this.props.GetInterlocutorAvatarThunk(DialoguserID)
        this.props.GetProfileStatusThunk(this.props.MyID)
    }


    render() {
        return (
            <MessagesWithUser {...this.props} />
        )
    }
}

let StateToProps = (state) => ({
    MessagesData: GetMessagesDataSelector(state),
    DialogsMessages : GetDialogsMessagesSelector(state),
    InterlocutorAvatar : GetinterlocutorAvatarSelector(state),
    UserAvatar : GetUserAvatarSelector(state),
    MyID : GetUserIDSelector(state)
})

export default compose(connect(StateToProps,
    {GetNewMessagesThunk,SendNewMessageThunk,
        GetInterlocutorAvatarThunk,setUserAvatarForDialogs,GetProfileStatusThunk}),
    AuthRedirect,
    withRouter)(MessagesContainer)
