import React from 'react';
import pc from './UserInfo.module.css'
import Preloader from "../../assetss/common/Loader/Loader";
import ProfileStatus from "../ProfileStatus";
import ProfileStatusF from "./ProfileStatusF";
import UserPhoto from "../../assetss/images/userPhoto.jpg";

const UserInfo = (props) => {
    debugger
if(!props.profile){
   return <Preloader/>
}

const OnPhotoSelected = (e) =>{
    if(e.target.files.length){
        props.SetProfilePhoto(e.target.files[0])
    }
}
    return <div className={pc.userdescrition}>
        <div className={pc.ProfileName}>
            {props.profile.fullName != null ?   props.profile.fullName  : null}
        </div>
        <div>
            <img className={pc.UserPhoto} src={(props.profile.photos.large && props.profile.photos.small) == null ? UserPhoto : props.profile.photos.large}/>
        </div>
        <div>
            {props.IsMyPage &&
            <div>
                <input className={pc.SelectPhoto} type={"file"}
                       onChange={OnPhotoSelected}
                accept="image/*"/>
            </div>
            }
        </div>


        <ProfileStatusF status={props.status}
                        SetProfileStatus={props.SetProfileStatus}
                        IsMyPage={props.IsMyPage}/>


        <div>
            {props.profile.lookingForAJob === true ? 'Im looking for a job' : 'Im had a job'}
        </div>
        <div>
            {props.profile.lookingForAJobDescription != null ? props.profile.lookingForAJobDescription : null}
        </div>
        <div>
            {props.profile.contacts.facebook != null ? 'Me in facebook : ' +props.profile.contacts.facebook : null}
        </div>
        <div>
            {props.profile.contacts.website != null ? 'Me in website : ' +props.profile.contacts.website : null}
        </div>
        <div>
            {props.profile.contacts.vk != null  ? 'Me in vk : ' +props.profile.contacts.vk : null}
        </div>
        <div>
            {(props.profile.contacts.twitter != null ) ? 'Me in twitter : ' +props.profile.contacts.twitter : null}
        </div>
        </div>
}
export default UserInfo;
