import React from 'react';
import {addpost_AC, type_post_info__AC} from "../../../data/ProfileReduser";
import MyPosts from "./MyPosts";


const MyPostsContainer = (props) => {


    let addPost = () => {
        props.dispatch(addpost_AC())

    }

    let updatePostText= (text) =>{

        props.dispatch(type_post_info__AC(text))
    }

    return (<MyPosts updatePostText={updatePostText}
                     addPost={addPost}
                     posts={props.ProfileData.Posts}
                     TextNewPost={props.ProfileData.TextNewPost}/>);
};
export default MyPostsContainer;
