import React from 'react';
import pc from './MyPosts.module.css'
import Post from "./Post/Post";
import {Field, reduxForm} from "redux-form";
import {required,MaxLengthCreator} from "../../utils/validators";

const maxValue20 = MaxLengthCreator(20)

const MyPostForm = (props) => {
    return <div>

        <form onSubmit={props.handleSubmit}>
            <div>
                <Field name={'postArea'}
                       placeholder={"type any info here"}
                       component={"textarea"}
                validate={[required,maxValue20]}/>

            </div>

            <div>
                <button> Send post</button>
            </div>
        </form>
    </div>
}

const ReduxMyPostForm = reduxForm({form: 'mypost'})(MyPostForm)



const MyPosts = (props) => {
    let SendPost = (formDatas) => {
        console.log(formDatas)
    /*    props.typePostInfo(formDatas.postArea)*/
        props.addpost(formDatas.postArea)


        //here is all datas of forms, we can do here use some thunk for a make request on server
    }

    let Posts = props.posts.map(p => <Post message={p.Post} likecount={p.likesCount}/>);

    return (

        <div className={pc.pp}>
            <h3>My posts</h3>
            <ReduxMyPostForm onSubmit={SendPost}/>
            <div className={pc.marginPost}>
                {Posts}
            </div>
        </div>
    )
}
export default MyPosts;
