import {AuthMeThunk} from "../data/AuthReducer"

const INITIALIZATED = 'INITIALIZATED'

let DefaultState = {
   inition : false
}

const InitializateReducer = (state = DefaultState, action) => {
    switch (action.type) {

        case INITIALIZATED : {
            return {
                ...state,
                inition : true
            }

        }

        default :
            return state

    }
}

export const initializating = () => ({type: INITIALIZATED})


