import React from 'react'
import d from './../Dialogs.module.css';
import {addpost_AC, type_message_info__AC,sendmessage_AC } from "../../../data/datas";



const Message = (props) => {
    let NewMessage = React.createRef();



    let sendmessage_AC = () => {
        props.dispatch(addpost_AC())

    }


    let OnPostChange= () =>{
        let text = NewMessage.current.value
        props.dispatch(type_message_info__AC(text))
    }



    return (
        <div className={d.messages} >
            {/*{props.message}*/}
         <textarea
             ref={NewMessage}
             value={props.NewMessageText}
             onChange={OnPostChange}>
         </textarea>
            <div>
            <button onClick={sendmessage_AC}> send message </button>
            </div>
        </div>

    )
}
export default Message;
