import React, {useState} from 'react';
import IconButton from '@material-ui/core/IconButton';
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import ListMenuIcon from '@material-ui/icons/MoreVert';
import hs from "./Header.module.css"
import DeleteToDoListButton from "./DeleteToDoList";
import EditToDoListTitleButton from "./EditToDoListTitle";


const ToDoListMenuIcon = (props) => {

    const [anchorEl, setAnchorEl] = useState(null);
    const isMenuOpen = Boolean(anchorEl);


    const handleProfileMenuOpen = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };


    const menuId = 'todo_list_menu_id';
    const renderMenu = (
        <Menu
            anchorEl={anchorEl}
            anchorOrigin={{vertical: 'top', horizontal: 'right'}}
            id={menuId}
            keepMounted
            transformOrigin={{vertical: 'top', horizontal: 'right'}}
            open={isMenuOpen}
            onClose={handleMenuClose}
        >
            <div className={hs.accountIconUserName}>
                {props.UserProfile && props.UserProfile.fullName}
            </div>

            <MenuItem onClick={handleMenuClose}>
                <EditToDoListTitleButton />
            </MenuItem>

            <MenuItem >
                <DeleteToDoListButton />
            </MenuItem>
        </Menu>
    );


    return <div>
        <IconButton
            edge="end"
            size="small"
            aria-label="ToDo list menu"
            aria-controls={menuId}
            aria-haspopup="true"
            onClick={handleProfileMenuOpen}
            color="primary"
        >
            <ListMenuIcon />
        </IconButton>
        {renderMenu}
    </div>

}


export default ToDoListMenuIcon;
