import React, {useEffect,useState} from "react"
import {MaxLengthCreator, required} from "../utils/validators";
import FUStyles from "./FindUser.module.css";
import {Field, reduxForm} from "redux-form";
import {FindUserFormInput} from "../assetss/common/ValidatorsComponents/ElementsValidators";
import lm from "../Login/login.module.css";


const maxValue50 = MaxLengthCreator(50)

const FindUserForm = (props) => {
    debugger
    let [FoundedUser,SetFoundedUser] = useState(props.FoundedUser)
    useEffect(() => {
            SetFoundedUser(props.FoundedUser)
        }, [props.FoundedUser]
    )
    return (
        <form onSubmit={props.handleSubmit}>
            <div >

                <div className={FUStyles.FindUserInput}>
                    <div>
                        <div>
                            <Field name={"UserNameToFinding"}
                                   component={FindUserFormInput}
                                   placeholder={"Enter user name to finding"}
                                   validate={[required, maxValue50]}/>
                        </div>
                        <div>
                            {props.error
                                ?
                                <div className={lm.AuthError}>
                                    {props.error}
                                </div>
                                : null}
                                <div>
                                    {FoundedUser && FoundedUser.items.length === 0 ? "no one user with this name" :null}
                                </div>
                            {FoundedUser &&

                            FoundedUser.items.map(item => <span className={FUStyles.distance}>
                                {item.id}

                            </span>)}
                            <div>
                                <button className={FUStyles.FindUserButtons}>
                                    Find
                                </button>
                                &nbsp;&nbsp; <button className={FUStyles.FindUserButtons}
                                                     onClick={props.FoundingModeOFF}>
                                go back </button>
                            </div>


                        </div>
                    </div>
                </div>
            </div>
        </form>
    )
}

const ReduxMessageWithUserForm = reduxForm({form: 'FindUser'})(FindUserForm)


const FindUser = (props) => {


    let [FoundingMode, SetFoundingMode] = useState(false)

    const FoundingModeON = () =>{
        SetFoundingMode(true)

    }
    const FoundingModeOFF = () =>{
        SetFoundingMode(false)
    }


    const FindUser =(OnSubmitData)=>{
        console.log(OnSubmitData)
        props.FindUserThunk(OnSubmitData.UserNameToFinding)
    }


    return <div>
        {!FoundingMode ?
            <button onClick={FoundingModeON}> find user </button>
            : <ReduxMessageWithUserForm FoundedUser={props.FoundedUser}
                                        onSubmit={FindUser}
                                        FoundingModeOFF={FoundingModeOFF}

            />


        }

    </div>

}
export default FindUser
