const add_post = 'ADD-POST'
const type_post_info = 'TYPES-POST-INFO'
const type_message_info = 'TYPES-MESSAGE-INFO'
export let store = {
    _state: {
        ProfileData: {
            Posts: [
                {id: 1, Post: 'post1', likesCount: 12},
                {id: 2, Post: 'post2', likesCount: 2},
                {id: 3, Post: 'post3', likesCount: 11},
                {id: 4, Post: 'post4', likesCount: 5},
                {id: 5, Post: 'post5', likesCount: 121}
            ],
            TextNewPost: ''
        },
        MessagesData: {
            users: [
                {id: 1, name: 'Vlad'},
                {id: 2, name: 'Oleg'},
                {id: 3, name: 'Sveta'},
                {id: 4, name: 'Katya'},
                {id: 5, name: 'Olya'}
            ],
            messages: [
                {id: 1, message: 'hi'},
                {id: 2, message: 'yo'},
                {id: 3, message: 'hello'},
                {id: 4, message: 'ky'},
                {id: 5, message: 'privet'}
            ],
            NewMessageText: '',

            avatars: [
                {
                    id: 1,
                    avalink: 'https://avatars.mds.yandex.net/get-pdb/988157/7bf97b91-4108-443f-b03a-9c0881ac5cb6/s1200?webp=false'
                },
                {
                    id: 2,
                    avalink: 'https://get.wallhere.com/photo/mountains-animals-flowers-looking-away-anime-anime-girls-animal-ears-blue-eyes-water-brunette-glasses-bicycle-sky-sitting-clouds-fish-butterfly-fishing-screenshot-55652.jpg'
                },
                {id: 3, avalink: 'https://s1.1zoom.ru/b5050/652/296854-Sepik_2048x1152.jpg'},
                {id: 4, avalink: 'https://c.wallhere.com/photos/04/6a/anime_anime_girls-1525953.jpg!d'},
                {id: 5, avalink: 'https://wallbox.ru/wallpapers/main/201329/anime-kartinka-yepizod-1ac0544.jpg'}
            ]
        },
        sidebar: {
            friendsNames: [
                {id: 1, name: 'Katya'},
                {id: 2, name: 'Olya'},
                {id: 3, name: 'Anya'}
            ]

        }
    },
    _callSubscriber() {
        console.log('')
    },
    getState() {
        return this._state
    },
    subscribe(observer) {
        this._callSubscriber = observer
    },

    dispatch(action) {
        if (action.type === 'ADD-POST')
        {
            let NewPost = {
                id: 5,
                Post: this._state.ProfileData.TextNewPost,
                likesCount: 0
            }
            this._state.ProfileData.Posts.push(NewPost);
            this._state.ProfileData.TextNewPost = '';
            this._callSubscriber(this._state)
        }
        else if (action.type === 'TYPES-POST-INFO')
        {
            this._state.ProfileData.TextNewPost = action.newtext
            this._callSubscriber(this._state)
        }
        else if (action.type === 'TYPES-MESSAGE-INFO')
        {
            this._state.MessagesData.NewMessageText = action.NewMessageText
            this._callSubscriber(this._state)
        }
        else if (action.type === SEND_MESSAGE)
        {
            let body = this._state.MessagesData.NewMessageText
            this._state.MessagesData.messages.push({id: 6, message: body})
            this._callSubscriber(this._state)
        }
    },


}
export const addpost_AC = () => {
    return {type : add_post }
}
export const type_post_info__AC = (text) => {
    return {type : type_post_info, newtext:text }
}
export const type_message_info__AC = (text) => {
    return {type : type_message_info, NewMessageText:text }
}
export default store
window.store = store
