import React from 'react'
import {connect} from "react-redux";
import Users from "./Users";
import {follow_AC, setUsers_AC, unfollow_AC,setCurrentPage_AC} from "../../data/UsersReduser";

let StateToProps = (state)=>{
    return{
        Users: state.UsersData.Users,
        pageSize : state.UsersData.pageSize,
        totalUsersCount: state.UsersData.totalUsersCount,
        currentPage : state.UsersData.currentPage
    }
}

let DispatchToProps = (dispatch)=>{
    return{onFollow: (userID)=>{

            dispatch(follow_AC(userID))
        },
        onUnFollow: (userID) =>{

            dispatch(unfollow_AC(userID))
        },
        setUsers:(Users)=>{
        dispatch(setUsers_AC(Users))
        },
        SetCurrentPage: (NumberOfPage) =>{
        dispatch(setCurrentPage_AC(NumberOfPage))
        }
    }
}

const UsersContainer = connect(StateToProps,DispatchToProps)(Users)

export default UsersContainer
